package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Byte min and max values
		
		byte byteMinVal = Byte.MIN_VALUE;
		byte byteMaxVal = Byte.MAX_VALUE;
		
		System.out.println("Byte Minimal Value = " + byteMinVal);
		System.out.println("Byte Maximal Value =  " + byteMaxVal);
		
		byte b = 24;
		System.out.println("Byte value = " + b);
		System.out.println();
		// Short min and max values
		
		short shortMinVal = Short.MAX_VALUE;
		short shortMaxVal = Short.MAX_VALUE;
		
		System.out.println("Short Minimal Value = "+ shortMinVal);
		System.out.println("Short Maximal Value = "+ shortMaxVal);
		
		short s = -555;
		System.out.println("Short value = " + s);
		System.out.println();
		// Int min and max values
		
		int intMinVal = Integer.MIN_VALUE;
		int intMaxVal = Integer.MAX_VALUE;
		
		System.out.println("Int Minimal Value = " + intMinVal);
		System.out.println("Int Maximal Value = " + intMaxVal);
		
		int i = 2146483;
		System.out.println("Int Value = " + i);
		System.out.println();
		// Long min and max values
		
		long longMinVal = Long.MIN_VALUE;
		long longMaxVal = Long.MAX_VALUE;
		
		System.out.println("Long Minimal Value = " + longMinVal);
		System.out.println("Long Maximal Value = " + longMaxVal);
		
		long l = 922337203685477580l;
		System.out.println("Long value = " + l);
		System.out.println();
		
		// Double min and max values 
		
		double doubleMinVal = Double.MIN_VALUE;
		double doubleMaxVal = Double.MAX_VALUE;
		
		System.out.println("Double Minimal Value = " + doubleMinVal);
		System.out.println("Double Maximal Value = " + doubleMaxVal);
		
		double d = 13.04123d;
		System.out.println("Double value = "+ d);
		System.out.println();
		
		// Float min and max values
		
		float floatMinVal = Float.MIN_VALUE;
		float floatMaxVal = Float.MAX_VALUE;
		
		System.out.println("Float Minimal Value = " + floatMinVal);
		System.out.println("Float Maximal Value = " + floatMaxVal);
		
		float f = 1.11111111111111111111f;
		System.out.println("Float value = " + f);
		System.out.println();
		
		char samsung = 'a';
		System.out.println("My char is: " + samsung);
		System.out.println();
		
		boolean x = 2 > 3;
		
		System.out.println("Is 2 bigger than 3? - " + x);
		
		
	}

}
